    public static int expressiveWords(String s, String[] words) {
        int count = 0;
        for (String word : words) {
            if(s.length() < word.length()){
                break;
            }
            int sIndex = 0;
            int wIndex = 0;
            boolean charMatch = true;
            while(sIndex < s.length() && wIndex < word.length()){
                char temp = word.charAt(wIndex);
                boolean rep = false;
                int countW = 0;
                while(wIndex < word.length() && word.charAt(wIndex) == temp){
                    countW++;
                    wIndex++;
                }
                int countS = 0;
                while(sIndex < s.length() && s.charAt(sIndex) == temp){
                    countS++;
                    sIndex++;
                }
                if(countS == 0 || (countS == 1 && rep) || (countS == 2 && countW == 1) || (countS < countW)){
                    charMatch = false;
                    break;
                }
            }
            if(charMatch && sIndex == s.length() && wIndex == word.length()){
                count++;
            }
        }
        return count;
    }
